import React, { useState } from "react";
import "./TopBar.css";

export default function TopBar() {
  const user = { name: "Atif", credits: 1250 };
  const unread = 1;

  const [open, setOpen] = useState(false);

  const handleLogout = () => {
    alert("User logged out!");
    setOpen(false);
  };

  return (
    <div className="topbar">
      <div className="brand">Chat with AI</div>

      <div className="right">
        <div className="credits">
          <button className="credit-btn">
            <span className="icon">üïí</span> {user.credits.toLocaleString()}
          </button>
        </div>

        <div className="notif">
          <span className="bell">üì¢</span>
          {unread > 0 && <span className="notif-badge">{unread}</span>}
        </div>

        <div className="profile" onClick={() => setOpen(!open)}>
          <div className="avatar">üßë</div>
          <span className="name">{user.name}</span>
          <span className="arrow">‚¨áÔ∏è</span>

          {open && (
            <div className="dropdown">
              <button onClick={handleLogout}>Logout</button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
